<?phpclass samplework extends controller{    public function __construct()    {    }    public function index($service_id)    {        $sampleworks = $this->model->get_samplework($service_id);        $data = [            'sampleworks' => $sampleworks,        ];        $this->viewUrl('sampleworks/index' , $data);    }    public function all()    {        $sampleworks = $this->model->get_sampleworks();        $data = [            'sampleworks' => $sampleworks,        ];        $this->viewUrl('sampleworks/index' , $data);    }}