<style>    .account {        overflow: hidden;        min-height: 100vh;        display: flex;        /* Horizontal centering (main axis): */        justify-content: center;        /* Vertical centering (cross axis): */        align-items: center;        padding: 0 20px;    }    .form-content {        margin-bottom: 35px;    }    .form-account .txt {        height: 45px;    }    .form {        max-width: 400px;        width: 100%;        background: #fff;        display: -ms-flexbox;        display: -webkit-box;        display: flex;        -ms-flex-pack: center;        -webkit-box-pack: center;        justify-content: center;        flex-wrap: wrap;        -ms-flex-wrap: wrap;        padding: 2em 2em 3em;        box-shadow: 0 10px 40px -14px rgba(0, 0, 0, 0.25);        position: relative;    }    .txt ::placeholder {        padding: 0 12px;        text-align: right !important;    }    .txt.txt-l::placeholder {        text-align: right !important;    }    .txt.txt-l {        text-align: left;    }    .txt {        width: 100%;        padding: 0 12px;        border-radius: 3px;        height: 42px;        line-height: 40px;        border: solid 2px #e2e2e2;        margin-bottom: 15px;        text-align: right;        color: #444444;    }    .btn-login{        /*width: 95px;*/        font-size: .8rem;        padding: 0 30px;        font-weight: 500;        line-height: 47.4px;        background: #46b2f0;        text-align: center;        color: #fff !important;    }    .btn-login {        border-radius: 0 3px 3px 0;        margin-left: 1px;    }    .btn {        color: #fff;        font-size: 15px;        font-weight: 500;        text-align: center;        cursor: pointer;        padding: 0.73em 5em;        margin: 1em 0;        border-radius: 0px;        background: #46b2f0;        position: relative;        overflow: hidden;        white-space: nowrap;        border-radius: 3px;    }</style><main>    <div class="account">        <form action="<?= URL . "/login/verify" ?>" class="form" method="post">            <div class="form-content form-account">                <p> صفحه ورود به پنل مدیریت</p>                <input name="email" type="text" class="txt-l txt" placeholder="ایمیل" >                <input name="password" type="text" class="txt-l txt" placeholder="رمز ورود">                <br>                <button class="btn btn-login">ورود</button>            </div>        </form>    </div></main>